import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';import 'package:shiftapp/presentation/shared/components/image_builder.dart';import '../../../../../utils/app_icons.dart';import '../../../../presentationUser/resources/colors.dart';import '../../../../presentationUser/resources/constants.dart';import '../../../../shared/components/base_stateless_widget.dart';class HeaderItemWidget extends BaseStatelessWidget {  final DataHeader dataHeader;  HeaderItemWidget({required this.dataHeader});  @override  Widget build(BuildContext context) {    return Row(      children: [        kBuildCircleImage(dataHeader.companyLogo),        SizedBox(          width: 10,        ),        Expanded(          child: Column(            mainAxisAlignment: MainAxisAlignment.start,            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(                dataHeader.companyName,                style: kTextSemiBold.copyWith(color: kDark, fontSize: 20),              ),              SizedBox(                height: 5,              ),              dataHeader.design == 2 ? itemLabelValue(label: strings.occupation, value: dataHeader.job) : SizedBox(),              dataHeader.design == 1 ? itemLabelValue(label: strings.date_dispose, value: dataHeader.dateDispose) : SizedBox(),            ],          ),        ),        dataHeader.design == 1            ? Column(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [SizedBox(), valueRow(value: dataHeader.money)],              )            : SizedBox(),      ],    );  }  valueRow({required String value}) {    return Row(      children: [        Text(          value,          style: kTextBold.copyWith(color: kPrimary, fontSize: 15),          textAlign: TextAlign.center,        ),        Text(          "${strings.rs} ",          style: kTextMedium.copyWith(color: kPrimary, fontSize: 12),          textAlign: TextAlign.center,        ),        SizedBox(          width: 10,        ),        SvgPicture.asset(AppIcons.exchange),      ],    );  }  itemLabelValue({    required String label,    required String value,  }) {    return Container(      height: 30,      child: Row(        children: [          Text(            "${label} : ",            style: kTextBold.copyWith(color: kPrimary, fontSize: 10),          ),          SizedBox(            width: 5,          ),          Text(            value,            style: kTextMedium.copyWith(color: kBattleShipGrey, fontSize: 10),          ),        ],      ),    );  }}class DataHeader {  final String companyLogo;  final String companyName;  final String job;  final int design;  final String dateDispose;  final String money;  DataHeader({required this.companyLogo, required this.companyName, required this.job, required this.design, required this.dateDispose, required this.money});}