import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';import '../../../../../domain/entities/shared/date_formatter.dart';import '../../../../../utils/app_icons.dart';import '../../../../presentationUser/common/common_state.dart';import '../../../../presentationUser/resources/colors.dart';import '../../../../presentationUser/resources/constants.dart';import '../../../../shared/components/base_stateless_widget.dart';import '../../../../shared/components/decorations/decorations.dart';import '../../domain/entities/WalletBalance.dart';class HeaderAdminWalletWidget extends BaseStatelessWidget {  final StreamStateInitial<WalletBalance> walletBalanceStream;  HeaderAdminWalletWidget({required this.walletBalanceStream});  @override  Widget build(BuildContext context) {    String date = DateFormatter.getCurrentDate();    return StreamBuilder<WalletBalance>(        stream: walletBalanceStream.stream,        builder: (context, snapshot) {          return SizedBox(            height: 220,            child: Stack(              children: [                Container(                  height: 175,                  padding: const EdgeInsets.only(top: 24),                  width: double.infinity,                  decoration: Decorations.createRectangleDecorationOnlyBottom(),                  child: Text(                    strings.wallet,                    style: kTextMedium.copyWith(color: kWhiteF2),                    textAlign: TextAlign.center,                  ),                ),                Positioned(                    top: 60,                    left: 10,                    right: 10,                    child: Column(                      children: [                        Text(                          strings.current_balance,                          style: kTextMedium.copyWith(color: kOrange7f),                          textAlign: TextAlign.center,                        ),                        Padding(                          padding: const EdgeInsets.symmetric(vertical: 7),                          child: Text(                            " ${snapshot.data?.currentBalanceValue ?? '0'} ${strings.sr}",                            style: kTextMedium.copyWith(color: kWhite, fontSize: 26),                            textAlign: TextAlign.center,                          ),                        ),                        Text(                          date,                          style: kTextMedium.copyWith(color: kOrange7f),                          textAlign: TextAlign.center,                        ),                      ],                    )),                Positioned(                  top: 150,                  left: 10,                  right: 10,                  child: Container(                    decoration: Decorations.decorationConditions,                    padding: EdgeInsets.symmetric(horizontal: 20, vertical: 10),                    height: 60,                    child: Row(                      crossAxisAlignment: CrossAxisAlignment.center,                      mainAxisAlignment: MainAxisAlignment.spaceBetween,                      children: [                        rowData(image: AppIcons.wallet3, image2: AppIcons.up, value: snapshot.data?.depositValue.toString() ?? '', title: strings.deposit),                        divider(),                        rowData(image: AppIcons.wallet4, image2: AppIcons.dwn, value: snapshot.data?.pendingAmounts.toString() ?? '', title: strings.salary_hanging),                        divider(),                        rowData(image: AppIcons.wallet1, image2: AppIcons.up, value: snapshot.data?.totalPayed.toString() ?? '', title: strings.payments),                      ],                    ),                  ),                ),              ],            ),          );        });  }  rowData({    required String image,    required String image2,    required String value,    required String title,  }) {    return Row(      crossAxisAlignment: CrossAxisAlignment.start,      mainAxisAlignment: MainAxisAlignment.start,      children: [        SvgPicture.asset(image),        SizedBox(          width: 10,        ),        Column(          crossAxisAlignment: CrossAxisAlignment.start,          mainAxisAlignment: MainAxisAlignment.start,          children: [            Text(              title,              style: kTextBold.copyWith(color: kPrimary, fontSize: 12),              textAlign: TextAlign.center,            ),            SizedBox(              width: 10,            ),            Row(              children: [                Text(                  value,                  style: kTextBold.copyWith(color: kPrimary, fontSize: 12),                ),                SizedBox(                  width: 5,                ),                SvgPicture.asset(image2),              ],            )          ],        )      ],    );  }  divider() {    return Container(      width: .5,      height: 20,      color: Colors.grey,    );  }}