import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../data/models/add_vehicle_zone_params.dart';import '../../../domain/entities/vehicle_zone.dart';import '../bloc/add_vehicles_zone_bloc.dart';import '../bloc/add_vehicles_zone_state.dart';import 'add_vehicles_zone_screen.dart';class AddInfoVehicleZonePage extends BaseBlocWidget<    Initialized<AddVehiclesZoneState>, AddVehiclesZoneCubit> {  final Function(AddVehicleZoneParams)? onNext;  final int Function()? idCallback;  AddInfoVehicleZonePage({super.key, this.onNext, this.idCallback});  @override  void loadInitialData(BuildContext context) {    VehicleZone? item = getArguments(context);    bloc.fetchInitialData(item);  }  @override  String? title(BuildContext context) {    return getArguments(context) != null ? strings.edit_data : null;  }  @override  Widget buildWidget(      BuildContext context, Initialized<AddVehiclesZoneState> state) {    return AddInfoVehicleZoneScreen(      state: state.data,      onFetchProjectsByCompanyId: (id) {        bloc.fetchProjectsCompanyId(id);      },      onFetchVehiclesAndShiftsByProjectId: (id) {        bloc.fetchVehiclesByProjectId(id);        bloc.fetchShiftByProjectId(id);      },      onAddVehiclesZone: (params) {        getArguments(context) != null            ? bloc.editVehiclesZone(params)            : onNext!(params);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }  @override  bool detectRequiredTasks() {    return false;  }}