import 'package:flutter/material.dart';import 'package:shiftapp/presentation/presentationUser/resources/colors.dart';import '../../../../../../utils/app_icons.dart';import '../../../../../presentationUser/resources/constants.dart';import '../../../../../shared/components/base/user_info_widget_with_icon.dart';import '../../../../../shared/components/base_stateless_widget.dart';import '../../../../../shared/components/decorations/decorations.dart';import '../../../../../shared/components/icons/icon_text.dart';import '../../../../../shared/components/texts/list_row_texts.dart';import '../../../domain/entities/ban_employee_details.dart';import '../../../domain/entities/job_evaluations_employee.dart';import '../widgets/statistics_employee.dart';class BanEmployeeDetailsScreen extends BaseStatelessWidget {  final BanEmployeeDetails details;  final List<JobEvaluationsEmployee> jobEvaluationsEmployee;  BanEmployeeDetailsScreen({    Key? key,    required this.details,    required this.jobEvaluationsEmployee,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Material(        color: kBackground,        child: SingleChildScrollView(          child: Column(            children: [              headerCv(),              StatisticsEmployee(                details: details,                jobEvaluationsEmployee: jobEvaluationsEmployee,              ),              informationBase(context),              informationWork(context),            ],          ),        ));  }  Widget headerCv() {    return Container(      margin: EdgeInsets.all(10),      padding: EdgeInsets.all(10),      decoration: Decorations.shapeDecoration(radius: 10),      child: UserInfoWidgetWithIcon(        image: details.logo ?? '',        name: details.name ?? '',        email: details.email,      ),    );  }  Widget informationBase(context) {    return Container(        margin: EdgeInsets.all(10),        padding: EdgeInsets.all(15),        width: MediaQuery.of(context).size.width * .98,        decoration: Decorations.shapeDecoration(radius: 10),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            iconText(text: strings.base_inf, icon: AppIcons.mainDataOutline),            SizedBox(              height: 10,            ),            ListRowTexts(              titleStyle:                  kTextMedium.copyWith(color: kPrimaryDark, fontSize: 14),              titles: [                strings.id_number,                strings.city,                strings.type,                strings.birthdate,              ],              values: [                details.idNumber ?? '',                details.city ?? '',                details.gender ?? '',                details.birthDate ?? '',              ],            ),          ],        ));  }  Widget informationWork(context) {    return Container(        margin: EdgeInsets.all(10),        padding: EdgeInsets.all(15),        width: MediaQuery.of(context).size.width * .98,        decoration: Decorations.shapeDecoration(radius: 10),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            iconText(),            SizedBox(              height: 10,            ),            ListRowTexts(              flex: 3,              titleStyle:                  kTextMedium.copyWith(color: kPrimaryDark, fontSize: 14),              titles: [                strings.level_computer,                strings.current_state,                strings.level_eng,                strings.level_education,                strings.favorite_jobs,              ],              values: [                details.computerLevel ?? '',                details.currentSituation ?? '',                details.englishLevel ?? '',                details.educationLevel ?? '',                details.favourateJob ?? '',              ],            ),          ],        ));  }  Widget iconText({String? text, String? icon}){    return      IconText(        icon: icon ?? AppIcons.bagWorkOutline,        text: text ?? strings.work_inf,        crossAxisAlignment: CrossAxisAlignment.center,        textStyle: kTextBodyBold.copyWith(            color: kRateBarIconColor,            fontSize: 16,            fontWeight: FontWeight.w700),      );  }}