import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart'; import '../bloc/add_cashifter_loan_bloc.dart';import '../bloc/add_cashifter_loan_state.dart';import 'add_cashifter_loan_screen.dart';class AddCashifterLoanPage extends BaseBlocWidget<    AddCashifterLoanState, AddCashifterLoanCubit> {  AddCashifterLoanPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {  bloc.fetchInitialData();  }  @override  String? title(BuildContext context) {    return strings.add_new_loan_request;  }  @override  bool detectRequiredTasks() {    return false;  }  @override  Widget buildWidget(      BuildContext context, AddCashifterLoanState state) {    return AddCashifterLoanScreen(      state: state,      onFetchCashifters: (companyId) {        bloc.fetchCashifters(companyId);      },      onAddCashifterLoan: (params) {        bloc.addNewLoanRequest(params);      },    );  }  @override  void onRequestSuccess(String? message) {    Navigator.pop(context, true);  }}