import 'package:json_annotation/json_annotation.dart';part 'classification_dto.g.dart';@JsonSerializable(ignoreUnannotated: false)class ClassificationDto {  @JsonKey(name: 'id')  final int? id;  @JsonKey(name: 'companyId')  final int? companyId;  @JsonKey(name: 'nameAr')  final String? nameAr;  @JsonKey(name: 'companyName')  final String? companyName;  @JsonKey(name: 'nameEn')  final String? nameEn;  @JsonKey(name: 'description')  final String? description;  @JsonKey(name: 'code')  final String? code;  @JsonKey(name: 'icon')  final String? icon;  ClassificationDto({this.id, this.companyName, this.nameAr, this.companyId, this.nameEn, this.description, this.code, this.icon});  factory ClassificationDto.fromJson(Map<String, dynamic> json) => _$ClassificationDtoFromJson(json);  Map<String, dynamic> toJson() => _$ClassificationDtoToJson(this);}