import '../../data/models/classification_dto.dart';class Classification {  Classification({    this.id,    this.companyName,    this.companyId,    this.nameAr,    this.nameEn,    this.description,    this.code,    this.icon,  });  int? id;  int? companyId;  String? nameAr;  String? companyName;  String? nameEn;  String? description;  String? code;  String? icon;//  ClassificationDto  factory Classification.fromDto(ClassificationDto json) {    return Classification(      id: json.id,      companyId: json.companyId,      companyName: json.companyName,      nameAr: json.nameAr,      nameEn: json.nameEn,      description: json.description,      code: json.code,      icon: json.icon,    );  }}