import '../../data/models/employee_data_dto.dart';class EmployeeDataEntity {  EmployeeDataEntity({    this.name,    this.mobile,    this.image,    this.id,  });  String? name;  String? mobile;  String? image;  int? id;  factory EmployeeDataEntity.fromDto(EmployeeDataDto json) {    return EmployeeDataEntity(name: json.name, image: json.image, id: json.id, mobile: json.mobile);  }}