import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base/stream_state_widget_v2.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../presentationUser/advancedFilter/widgets/text_field_search_job.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../domain/entities/EmployeeData.dart';import '../../bloc/add_new_employees_bloc.dart';import '../add/new_emp/add_new_employees_screen.dart';import 'home_add_employees_screen.dart';class HomeAddNewEmployeesPage extends BaseBlocWidget<    Initialized<List<EmployeeDataEntity>>, AddNewEmployeesCubit> {   HomeAddNewEmployeesPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {    bloc.fetchEmployee();  }   @override   bool detectRequiredTasks() {     return false;   }  @override  String? title(BuildContext context) {    return strings.add_cashifters;  }  TextEditingController searchController = TextEditingController();  @override  Widget buildWidget(      BuildContext context, Initialized<List<EmployeeDataEntity>> state) {    return StreamStateWidgetV2<List<EmployeeDataEntity>?>(        stream: bloc.employeeDataEntityStream,        initialData: state.data,        builder: (context, snapshot) {          return Column(            children: [              Padding(                padding: const EdgeInsets.only(                    left: 16, right: 16, top: 5, bottom: 10),                child: TextFieldSearchJob(                  title: strings.search_by_phone_employee,                  controller: searchController,                  isSuffixIcon: false,                  readOnly: false,                  onTap: () {},                  onChanged: (value) {                    bloc.fetchEmployeeStream(search: value);                  },                ),              ),              Expanded(                  child: HomeAddNewEmployeesScreen(                data: snapshot ?? [],              )),            ],          );        });  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}