import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import '../../../../../../data/repositories/resume/resume_repository.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../../projectsManagement/domain/entities/city.dart';import '../../../data/models/index.dart';@Injectable()class AddEmploymentOfficialCubit extends BaseCubit {  final EmploymentOfficialsRepository _repository;  final ResumeRepository _resumeRepository;  AddEmploymentOfficialCubit(this._repository, this._resumeRepository);  Future<void> addEmploymentOfficial(AddEmploymentOfficialParams params) async {    executeEmitterListener(() => _repository.addEditEmploymentOfficial(params));  }  Future<void> fetchAllaData() async {    executeBuilder(() => _resumeRepository.fetchAllCities(), onSuccess: (citiesItem){      final cities = citiesItem.map((e) => City(id: e.id, cityName: e.nameAr)).toList();      emit(Initialized<List<City>>(data: cities));    });  }}