import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../bloc/add_gas_stations_bloc.dart';import 'add_gas_stations_screen.dart';class AddGasStationsPage    extends BaseBlocWidget<InitializedGasStations, AddGasStationsCubit> {  @override  String? title(BuildContext context) {    return strings.add_gas_stations;  }  @override  void loadInitialData(BuildContext context) {    // TODO: implement loadInitialData    bloc.fetchData();  }  @override  Widget buildWidget(BuildContext context, InitializedGasStations state) {    return AddGasStationsScreen(      company: state.companies,      project: state.project,      addEditGasStation: (params) => bloc.addEditGasStation(params),      gasStation: getArguments(context),    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }  @override  bool detectRequiredTasks() {    return false;  }}