import 'package:shiftapp/domain/entities/shared/date_formatter.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import 'package:shiftapp/utils/app_icons.dart';import '../../../../domain/entities/GasStation.dart';import '../../../widgets/gas_stations_item.dart';class GasStationsScreen extends BaseStatelessWidget {  final List<GasStation> data;  final Function(int id) onDelete;  final Function() onRefresh;  GasStationsScreen({    required this.data,    required this.onDelete,    required this.onRefresh,  });  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      itemBuilder: (context, index) => GasStationsItem(data: data[index],onDelete: onDelete, onRefresh: onRefresh,),    );  }}