import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../domain/entities/index.dart';import '../bloc/add_type_violation_bloc.dart';import '../bloc/add_type_violation_state.dart';import 'add_type_violation_screen.dart';class AddTypeViolationPage extends BaseBlocWidget<Initialized<AddTypeViolationState>,    AddTypeViolationCubit> {  AddTypeViolationPage({super.key});  @override  void loadInitialData(BuildContext context) {    ViolationType? initialData = getArguments(context);    print('initialData: ${getArguments(context)}');    bloc.fetchInitialData(initialData?.companyId);  }  @override  bool detectRequiredTasks() {    return false;  }  @override  String? title(BuildContext context) {    return getArguments(context) == null        ? strings.add_violation_type        : strings.edit_violation_type;  }  @override  Widget buildWidget(      BuildContext context, Initialized<AddTypeViolationState> state) {    return AddTypeViolationScreen(      state: state.data,      initialData: getArguments(context),      onFetchProjectsByCompanyId: (id) {        bloc.fetchProjectsCompanyId(id);      },      onAdd: (params) {        bloc.addTypeViolation(params);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}