import '../../data/models/job_requirements_dto.dart';class JobRequirements {  JobRequirements({    this.id,    this.jobId,    this.descriptionEn,    this.descriptionAr,    this.jobName,  });  int? id;  int? jobId;  String? descriptionEn;  String? descriptionAr;  String? jobName;  factory JobRequirements.fromDto(JobRequirementsDto json) {    return JobRequirements(      id: json.id,      jobId: json.jobId,      jobName: json.jobName,      descriptionEn: json.descriptionEn,      descriptionAr: json.descriptionAr,    );  }}