import '../../data/models/uniform_dto.dart';class Uniform {  int? id;  String? name;  List<JobUniformDetail>? jobUniformDetails;  Uniform({    this.id,    this.name,    this.jobUniformDetails,  });  factory Uniform.fromDto(UniformDto json) {    return Uniform(      id: json.id,      name: json.name,      jobUniformDetails: json.jobUniformDetails,    );  }}// class JobUniformDetails {//   JobUniformDetails({//     this.jobUniformId,//     this.uniFormImage,//     this.description,//   });////   JobUniformDetails.fromJson(dynamic json) {//     jobUniformId = json['jobUniformId'];//     uniFormImage = json['uniFormImage'];//     description = json['description'];//   }//   int? jobUniformId;//   String? uniFormImage;//   String? description;////   Map<String, dynamic> toJson() {//     final map = <String, dynamic>{};//     map['jobUniformId'] = jobUniformId;//     map['uniFormImage'] = uniFormImage;//     map['description'] = description;//     return map;//   }////   factory JobUniformDetails.fromDto(JobUniformDetail json) {//     return JobUniformDetails(jobUniformId: json.jobUniformId, description: json.description, uniFormImage: json.uniFormImage);//   }// }