import 'package:flutter/material.dart';import '../../../../../shared/components/base_stateless_widget.dart';import '../../../../addOpportunity/domain/entities/opportunity_applicant.dart';import '../../../../employmentManagement/data/models/add_block_employee_params.dart';import '../../../../shared/domain/entities/opportunities/index.dart';import '../../../widgets/applicant_item.dart';import '../../bloc/applied_details_state.dart';class AppliedScreen extends BaseStatelessWidget {  final OpportunityApplicant data;  Function() onRefresh;  OpportunityDetailsData opportunityDetails;  Function(AddBlockEmployeeParams addBlockEmployeeParams) onBlockEmployee;  AppliedScreen(      {required this.data,      required this.onRefresh,      required this.onBlockEmployee,      required this.opportunityDetails});  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.freeLanceApplys?.length,      itemBuilder: (context, index) {        return ApplicantItem(          navItems: data.navItems ?? [],          menuEmployee: data.freeLanceApplys![index],          onBlockEmployee: onBlockEmployee,          onRefresh: onRefresh,          opportunityDetails: opportunityDetails,        );      },    );  }}