import 'package:flutter/material.dart';import 'package:shiftapp/presentation/adminFeatures/projectsManagement/presentation/addNewProject/pages/addAdditionalLocations/widgets/locations_item_picker.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../../../presentationUser/resources/colors.dart';import '../../../../../../shared/components/base_stateless_widget.dart';import '../../../../data/models/location_project_params.dart';class AddAdditionalLocationsScreen extends BaseStatelessWidget {  final List<LocationProjectParams> locations;  final Function(LocationProjectParams) onEditLocation;  final Function(int) onDeleteLocation;  final Function() onNext;  AddAdditionalLocationsScreen({    Key? key,    required this.locations,    required this.onEditLocation,    required this.onDeleteLocation,    required this.onNext,  }) : super(key: key);  TextEditingController addLocationController = TextEditingController();  List<LocationProjectParams> additionalLocations = [];  TextEditingController controller = TextEditingController();  StreamStateInitial<List<LocationProjectParams>> locationsStream =      StreamStateInitial<List<LocationProjectParams>>();  List<LocationProjectParams> locationsLatLng = [];  @override  Widget build(BuildContext context) {    return Material(        color: kGray_F5,        child: ListView.separated(          itemCount: locations.length,          shrinkWrap: true,          physics: NeverScrollableScrollPhysics(),          itemBuilder: (context, index) => LocationItemPicker(            item: locations[index],            onDelete: () => onDeleteLocation(locations[index].id ?? 0),            onEdit: (params){              params.id = locations[index].id;              onEditLocation(params);            },          ),          separatorBuilder: (context, index) => Divider(            color: kGrey_4,            height: 1,            endIndent: 20,            indent: 20,            thickness: 1,          ),        ));  }}