import 'package:flutter/material.dart';import 'package:shiftapp/presentation/adminFeatures/projectsManagement/presentation/addNewProject/pages/map_project_gates/widget/map_project_widget.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import 'package:shiftapp/utils/app_icons.dart';import '../../../../../../../presentationUser/resources/colors.dart';import '../../../../../../../presentationUser/resources/constants.dart';import '../../../../../data/models/employe_point_dto.dart';class EmployeeGatesScreen extends BaseStatelessWidget {  List<EmployePointDto>   data;  EmployeeGatesScreen({required  this.data});  @override  Widget build(BuildContext context) {    return Container(      color: Colors.white,      child: ListView.builder(        padding: EdgeInsets.zero,        itemCount: data.length, itemBuilder: (context, index) =>employeeWidget(data:data[index]) ,),    );  }  employeeWidget({required EmployePointDto data }) {    return Container(      margin: EdgeInsets.only(bottom: 10, ),      padding:EdgeInsets.all( 6),      decoration:Decorations.shapeDecoration(radius: 15),      child: ListRowTextsIcons(icons: [        AppIcons.empName,        AppIcons.empJob,        AppIcons.empStatus,        AppIcons.empTime      ], titles: [        strings.employee_name,        strings.job,        strings.status,        strings.working_period      ], values: [        data.name??"",        data.job??"",       data.status??"",        "${strings.from} ${data.startShiftDateTime??""} ${strings.too}  ${data.endShiftDateTime??""} "      ]),    );  }}