import 'package:flutter/material.dart';import 'package:shiftapp/presentation/adminFeatures/projectsManagement/presentation/addNewProject/pages/projectSuccessfullyPage/pages/show_qr_code_page.dart';import 'package:shiftapp/presentation/presentationUser/resources/colors.dart';import 'package:shiftapp/presentation/presentationUser/salarydefinitionrequest/requests/widgets/warning_widget.dart';import 'package:shiftapp/presentation/shared/components/app_cupertino_button.dart';import 'package:shiftapp/presentation/shared/components/decorations/decorations.dart';import 'package:shiftapp/presentation/shared/components/image_builder.dart';import '../../../../../../../../core/services/routes.dart';import '../../../../../../../../utils/app_icons.dart';import '../../../../../../../presentationUser/resources/constants.dart';import '../../../../../../../shared/components/base_stateless_widget.dart';import '../../../../../../../shared/components/buttons/icon_text_button.dart';import '../../../../../domain/entities/project_management.dart';import '../../../../projectsManagement/pages/projects_management_page.dart';import '../../../../projectsManagement/widgets/project_management_item.dart';class ProjectSuccessfullyScreen extends BaseStatelessWidget {  final ProjectManagement project;  final Function() onShareQrCodeImage;  final Function() onDownLoadQrCodeImage;  ProjectSuccessfullyScreen({    Key? key,    required this.project,    required this.onShareQrCodeImage,    required this.onDownLoadQrCodeImage,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      padding: const EdgeInsets.only(left: 16, right: 16, bottom: 20),      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          Container(            margin: const EdgeInsets.only(bottom: 15, top: 20),            padding: const EdgeInsets.all(16),            decoration: Decorations.shapeDecoration(),            child: Column(              children: [                kLoadSvgInCirclePath(AppIcons.successAlert),                SizedBox(                  height: 10,                ),                Text(strings.project_added_successfully,                    style: kTextSemiBold.copyWith(                        fontSize: 22, color: kBlack_3C)),                SizedBox(                  height: 15,                ),                Text(strings.project_added_manage_projects_list,                    style: kTextMedium.copyWith(                        fontSize: 14, color: kGrey_50.withOpacity(0.7))),              ],            ),          ),          WarningWidget(              warningText: strings.you_can_download_project_now_share_it,              color: kPrimary),          SizedBox(            height: 15,          ),          ProjectManagementItem(            item: project,            isShowOptionsMenu: false,            onRefresh: () {},            moreFooter: _MoreFooter(              item: project,              onShareQrCodeImage: onShareQrCodeImage,              onDownLoadQrCodeImage: onDownLoadQrCodeImage,            ),          ),          SizedBox(            height: 15,          ),          AppCupertinoButton(            text: strings.back_to_projects,            onPressed: () {              Navigator.pop(context);              Navigator.pop(context, true);            },          ),        ],      ),    );  }}class _MoreFooter extends BaseStatelessWidget {  final ProjectManagement item;  final Function() onShareQrCodeImage;  final Function() onDownLoadQrCodeImage;  _MoreFooter({    Key? key,    required this.item,    required this.onShareQrCodeImage,    required this.onDownLoadQrCodeImage,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(right: 2, left: 2, bottom: 16),      child: Column(        children: [          Divider(            color: kGrey_CA,            thickness: 0.5,            height: 1,          ),          SizedBox(height: 10),          Row(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              IconTextButton(                text: strings.share,                icon: AppIcons.shareOutline,                onTap: () {                  onShareQrCodeImage();                },              ),              SizedBox(                height: 30,                width: 1,                child: VerticalDivider(                  color: kGrey_C9,                  width: 1,                ),              ),              IconTextButton(                text: strings.vision_qr_code,                icon: AppIcons.visibilityOutline,                onTap: () {                  Navigator.pushNamed(context, Routes.showQrCodePage,                      arguments: item);                },              ),              SizedBox(                height: 30,                width: 1,                child: VerticalDivider(                  color: kGrey_C9,                  width: 1,                ),              ),              IconTextButton(                text: strings.download_qr_code,                icon: AppIcons.downloadSquareOutline,                onTap: () => onDownLoadQrCodeImage(),              ),            ],          ),        ],      ),    );  }}