import 'package:flutter/material.dart';import 'package:shiftapp/presentation/adminFeatures/projectsManagement/presentation/addNewProject/pages/workingPeriodsProject/widgets/add_working_periods_project_builder.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../../../shared/components/base_stateless_widget.dart';import '../../../../data/models/add_working_period_params.dart';import '../../../../domain/entities/working_period.dart';import 'widgets/working_periods_project_item.dart';class WorkingPeriodsProjectScreen extends BaseStatelessWidget {  final int projectId;  final int companyId;  final List<WorkingPeriod> workingPeriods;  final Function(AddWorkingPeriodParams) onAddPeriod;  final Function(int) onDeletePeriod;  final Function() onRefresh;  WorkingPeriodsProjectScreen({    Key? key,    required this.projectId,    required this.companyId,    required this.workingPeriods,    required this.onAddPeriod,    required this.onDeletePeriod,    required this.onRefresh,  }) : super(key: key);  //List<List<AddWorkingPeriodParams>> workingPeriods = [];  StreamStateInitial<List<AddWorkingPeriodParams>> workingPeriodsStream = StreamStateInitial<List<AddWorkingPeriodParams>>();  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: workingPeriods.length,      shrinkWrap: true,      // physics: const NeverScrollableScrollPhysics(),      padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 8),      itemBuilder: (context, index) => WorkingPeriodsProjectItem(        item: workingPeriods[index],        onEdit: () {          showAddWorkingPeriodSheet(context, companyId: companyId, id: workingPeriods[index].id, projectId: projectId, title: strings.edit_period, onRefresh: () {            onRefresh();          });        },        onDelete: () => onDeletePeriod(workingPeriods[index].id ?? 0),      ),    );  }}