import '../../../data/models/opportunity_tabs_dto.dart';class OpportunityTabs {  OpportunityTabs({    this.id,    this.name,  });  OpportunityTabs.fromJson(dynamic json) {    id = json['id'];    name = json['name'];  }  int? id;  String? name;  factory OpportunityTabs.fromDto(OpportunityTabsDto dto) {    return OpportunityTabs(      name: dto.name,      id: dto.id,    );  }}