import '../../data/models/termand_condition_dto.dart';class TermandCondition {  TermandCondition({    this.id,    this.conditionEn,    this.conditionAr,    this.companyId,    this.companyName,  });  int? id;  int? companyId;  String? conditionEn;  String? companyName;  String? conditionAr;  factory TermandCondition.fromDto(TermandConditionDto json) {    return TermandCondition(      id: json.id,      companyName: json.companyName,      companyId: json.companyId,      conditionEn: json.conditionEn,      conditionAr: json.conditionAr,    );  }}