import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../data/models/request_track_attendance_departure_params.dart';import '../bloc/request_track_attendance_departure_bloc.dart';import 'request_track_attendance_departure_screen.dart';class RequestTrackAttendanceDeparturePage    extends BaseBlocWidget<UnInitState, RequestTrackAttendanceDepartureCubit> {  RequestTrackAttendanceDeparturePage({super.key});  @override  bool detectRequiredTasks() {    return false ;  }  @override  String? title(BuildContext context) {    return strings.request_track_attendance_departure;  }  @override  Widget buildWidget(BuildContext context, UnInitState state) {    return RequestTrackAttendanceDepartureScreen(      initialData: getArguments(context),      userByPhoneStream: bloc.userByPhoneStream,      onSearchEmployee: (String phoneOrIdNumber) {        bloc.fetchEmpByPhone(phoneOrIdNumber);      },      onAdd: (RequestTrackAttendanceDepartureParams params) {        bloc.createUpdateRequestTrackAttendanceDeparture(params);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}