import 'dart:io';import 'package:shiftapp/presentation/adminFeatures/vehicles/presentation/add/pages/covenantsVehicles/view/widgets/convenant_vehicle_item.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import '../../../../../../../presentationUser/resources/colors.dart';import '../../../../../../../presentationUser/workHazards/presentation/addWorkHazard/widgets/upload_files_list.dart';import '../../../../../../../shared/components/text_field/build_text_field_item.dart';import '../../../../../data/models/add_covenant_vehicle_params.dart';import '../../../../../domain/entities/covenant_vehicle.dart';class VehiclesCovenantScreen extends BaseStatelessWidget {  final List<CovenantVehicle> data;  final bool isEditable;  final Function() onRefresh;  final Function(int) onDelete;  final Function() onNext;  final Function() onPrevious;  VehiclesCovenantScreen({    Key? key,    required this.data,    required this.isEditable,    required this.onRefresh,    required this.onDelete,    required this.onNext,    required this.onPrevious,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(        backgroundColor: kBackground,        bottomNavigationBar: isEditable ? null : RowButtons(          textSaveButton: strings.next,          onSave: () {            onNext();          },          onCancel: () {            onPrevious();          },        ),        body: ListView.builder(      itemCount: data.length,      padding: EdgeInsets.all(16),      itemBuilder: (context, index) {        return CovenantVehicleItem(          item: data[index],          onRefresh: onRefresh,          onDelete: onDelete,        );      },    ));  }}