import 'package:shiftapp/presentation/adminFeatures/vehicles/presentation/view/widgets/insurance_item.dart';import '../../../../../../../presentationUser/attendance/facerecognation/index.dart';import '../../../../../../../shared/components/base_stateless_widget.dart';import '../../../../../data/models/insurances_dto.dart';import '../../../../../domain/entities/insurances.dart';class InsuranceScreen extends BaseStatelessWidget {  final List<Insurances> data;  final Function() onRefresh;  final Function(int id) onDelete;  InsuranceScreen({super.key, required this.data, required this.onRefresh, required this.onDelete});  @override  Widget build(BuildContext context) {    return ListView.builder(        itemCount: data.length,        itemBuilder: (context, index) {          return InsuranceItem(data: data[index], onDelete: onDelete, onRefresh: onRefresh);        });  }}