import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/adminFeatures/vehicles/data/models/vehicle_details_dto.dart';import 'package:shiftapp/presentation/adminFeatures/vehicles/presentation/view/pages/vehicle_details/vehicles_details_screen.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../domain/entities/vehicle_details.dart';import '../../bloc/vehicles_cubit.dart';class VehiclesDetailsPage    extends BaseBlocWidget<Initialized<VehicleDetails>, VehiclesCubit> {  VehiclesDetailsPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {    bloc.fetchVehiclesById(getArguments(context));  }  @override  String? title(BuildContext context) {    return strings.vehicles_details;  }  @override  bool detectRequiredTasks() {    return false;  }  TextEditingController controller = TextEditingController();  @override  Widget buildWidget(BuildContext context, Initialized<VehicleDetails> state) {    return VehiclesDetailsScreen(data: state.data);  }}