import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../bloc/add_vehicle_violation_bloc.dart';import '../bloc/add_vehicle_violation_state.dart';import 'add_violation_vehicle_screen.dart';class AddVehicleViolationPage extends BaseBlocWidget<Initialized<AddVehicleViolationState>,    AddVehicleViolationCubit> {  AddVehicleViolationPage({super.key});  @override  void loadInitialData(BuildContext context) {    int? id = getArguments(context);    bloc.fetchInitialData(id);  }  @override  bool detectRequiredTasks() {    return false;  }  @override  String? title(BuildContext context) {    return getArguments(context) == null        ? strings.add_violation        : strings.edit_violation;  }  @override  Widget buildWidget(      BuildContext context, Initialized<AddVehicleViolationState> state) {    return AddVehicleViolationScreen(      state: state.data,      onAddVehicleViolation: (params, files) {        bloc.addVehicleViolation(params, files: files);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}