import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../data/repositories/vehicles_repository.dart';import '../../../../domain/entities/vehicle_violation.dart';@Injectable()class VehiclesCubit extends BaseCubit {  final VehiclesRepository _repository;  VehiclesCubit(    this._repository,  );  List<ContractViolation> searchList = [];  fetchVehicleViolations() async {    executeBuilder(() => _repository.fetchVehicleViolations(),        onSuccess: (data) {      searchList = data;      emit(Initialized<List<ContractViolation>>(data: data));    });  }  void searchByText(String text) {    if (text.isEmpty) {      emit(Initialized<List<ContractViolation>>(data: searchList));    } else {      final search = searchList          .where((element) => element.companyName!.contains(text))          .toList();      emit(Initialized<List<ContractViolation>>(data: search));    }  }}