import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import '../../../../../common/common_state.dart';import '../../../../data/repositories/attendance_and_departure_notifications_repository.dart';import '../../../../domain/entities/AttendanceNotifiAction.dart';@Injectable()class AttendanceAndDepartureNotificationsCubit extends BaseCubit {  final AttendanceAndDepartureNotificationsRepository _repository;  AttendanceAndDepartureNotificationsCubit(    this._repository,  );  fetchAttendance() async {    executeBuilder(() => _repository.fetchAttendanceNotifications(),        onSuccess: (value) {      final data = value.map((e) => AttendanceNotifiAction.fromDto(e)).toList();      emit(Initialized<List<AttendanceNotifiAction>>(data: data));    });  }}