import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../domain/entities/loans_request/loan_type.dart';import '../../../common/common_state.dart';import '../bloc/add_loan_request_bloc.dart';import 'add_loan_request_screen.dart';class AddLoanRequestPage extends BaseBlocWidget<    Initialized<List<LoanType>>, AddLoanRequestCubit> {  AddLoanRequestPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {  bloc.fetchLoansTypes();  }  @override  bool initializeByKoin() {    return false;  }  @override  String? title(BuildContext context) {    return strings.add_new_loan_request;  }  @override  Widget buildWidget(      BuildContext context, Initialized<List<LoanType>> state) {    return AddLoanRequestScreen(      loanTypes: state.data,      loanNumMonthConditionStream: bloc.loanNumMonthConditionStream,      idNumberStream: bloc.idNumberStream,      onFetchLoanNumMonthCondition: (id) {        bloc.fetchLoanNumMonthsConditions(id);      },      addNewLoanRequest: (prams) {        bloc.addNewLoanRequest(prams);      },      fetchIdNumber: (idNumber) {        bloc.fetchNamedByIdNumber(idNumber);      },    );  }  @override  void onRequestSuccess(String? message) {    Navigator.pop(context);    super.onRequestSuccess(message);  }}