import 'package:flutter/material.dart';import '../../../resources/colors.dart';import '../../../resources/constants.dart';class SliderContiner extends StatelessWidget {  const SliderContiner({Key? key, required this.color, required this.text, required this.isActive}) : super(key: key); final Color color; final String text;  final bool isActive;  @override  Widget build(BuildContext context) {    return Container(      width: 80,      alignment: Alignment.center,      padding: const EdgeInsets.all(4),      height:30,      decoration:  BoxDecoration(      color: color,      borderRadius: isActive==false?      const BorderRadiusDirectional.only(        topEnd: Radius.circular(20),        bottomEnd:Radius.circular(20),      )          :const BorderRadiusDirectional.only(        topStart: Radius.circular(20),        bottomStart:Radius.circular(20),      ),    ) ,      child: Text(text,style:kTextMedium .copyWith(color: kWhite,fontSize: 10), textAlign: TextAlign.center,),);  }}