import 'package:flutter/material.dart';import '../../../../shared/components/base_stateless_widget.dart';import '../../../resources/colors.dart';import '../../../resources/constants.dart';class TabViewOverView extends BaseStatelessWidget {  TabViewOverView({    Key? key,    this.onTabChange,  }) : super(key: key);  final Function(int)? onTabChange;  TabController? tabController;  @override  Widget build(BuildContext context) {    return DefaultTabController(        length: 3, // length of tabs initialIndex: 0,        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 5.0),          child: TabBar(            indicatorColor: Colors.orangeAccent,            controller: tabController,            unselectedLabelColor: kPrimary.withOpacity(.3),            isScrollable: true,            labelPadding: const EdgeInsetsDirectional.fromSTEB(0, 0, 20, 0),            indicator: const UnderlineTabIndicator(                borderSide: BorderSide(color: Colors.orange),                insets: EdgeInsets.only(left: 20, bottom: 10)),            labelStyle: kTextBodyBold.copyWith(              fontSize: 14,              color: kPrimary.withOpacity(.3),            ),            labelColor: kPrimary,            indicatorPadding: const EdgeInsets.only(left: 30, top: 10),            unselectedLabelStyle: kTextBodyBold.copyWith(                fontSize: 14, color: kPrimary.withOpacity(.3)),            onTap: onTabChange,            tabs: <Widget>[              Tab(                  child: Text(                strings.all_jobs,              )),              Tab(                  child: Text(                strings.vip_offers,              )),              Tab(                  child: Text(                strings.favorite_jobs,              )),            ],          ),        ));  }}