import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';import '../../../resources/colors.dart';import '../../../resources/constants.dart';class TextAndValue extends StatelessWidget {  const TextAndValue(      {Key? key,      required this.image,      required this.text,      required this.value,      required this.isText,      required this.widget})      : super(key: key);  final String image;  final String text;  final String value;  final bool isText;  final Widget widget;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 4),      child: Row(        children: [          Padding(            padding: const EdgeInsetsDirectional.only(start: 8),            child: SvgPicture.asset(              image,              width: 14,              height: 14,            ),          ),          Expanded(            child: Row(              mainAxisAlignment: MainAxisAlignment.start,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                const SizedBox(                  width: 8,                ),                Text(text,                    style:                        kTextBoldSmall.copyWith(color: kPrimary, fontSize: 10)),                const SizedBox(                  width: 8,                ),                Expanded(                  child: isText == true                      ? Text(value,                          style: kTextBoldSmall.copyWith(                              color: Colors.black.withOpacity(.5),                              fontSize: 10),maxLines: 1,)                      : widget,                ),              ],            ),          ),        ],      ),    );  }}