import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/presentationUser/salarydefinitionrequest/newrequest/pages/salary_definition_request_screen.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../common/common_state.dart';import '../bloc/salary_definition_request_bloc.dart';class SalaryDefinitionRequestPage extends BaseBlocWidget<    InitializedAddSalaryDefinitionRequest, AddSalaryDefinitionRequestCubit> {  SalaryDefinitionRequestPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {    bloc.fetchTypeOFCommerce();  }  @override  bool initializeByKoin() {    return false;  }  @override  String? title(BuildContext context) {    return strings.salary_definition_request;  }  @override  Widget buildWidget(      BuildContext context, InitializedAddSalaryDefinitionRequest state) {    return SalaryDefinitionRequestScreen(      itemTypeOfCommerce: state.typeOfCommerce,      reasons: state.reasons,      onAddSalaryDefinition: (prams) {        bloc.addSalaryDefinition(addSalaryDefinitionRequestPrams: prams);      },    );  }  @override  void onRequestSuccess(String? message) {    Navigator.pop(context);    super.onRequestSuccess(message);  }}