import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import '../../../../data/models/add_round_trip_fill_station_prams.dart';import '../../../../data/repositories/vehicles_operation_repository.dart';@Injectable()class GasStationsPlaneCubit extends BaseCubit {  final ReceiveVehiclesRepository _repository;  GasStationsPlaneCubit(this._repository);  void fetchInitialData(int roundTripId) async {    executeEmitterData(() => _repository.fetchGasStationTrip(roundTripId));  }  addRoundTripFillStation(AddRoundTripFillStationPrams params) {    executeEmitterListener(      () => _repository.addRoundTripFillStation(params),    );  }}