import 'package:get/get_utils/src/platform/platform.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:shiftapp/presentation/presentationUser/resources/constants.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import 'package:shiftapp/utils/app_icons.dart';import 'package:url_launcher/url_launcher.dart';import '../../../../../../../utils/app_utils.dart';import '../../../../../resources/colors.dart';import '../../../../data/models/add_round_trip_fill_station_prams.dart';import '../../../../domain/entities/gas_station_trip.dart';import '../../../../domain/entities/index.dart';class GasStationsPlaneItem extends BaseStatelessWidget {  final Function(AddRoundTripFillStationPrams params, LatLng latLng)      onAddRoundTripFillStation;  final GasStationTrip item;  final CurrentRoundTrip currentRoundTrip;  GasStationsPlaneItem({    Key? key,    required this.item,    required this.currentRoundTrip,    required this.onAddRoundTripFillStation,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.all(10),      padding: EdgeInsets.all(10),      decoration: Decorations.decorationTabs,      child: Column(        mainAxisAlignment: MainAxisAlignment.start,        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Row(            mainAxisAlignment: MainAxisAlignment.start,            crossAxisAlignment: CrossAxisAlignment.start,            children: [              kLoadSvgInCirclePath(AppIcons.gasStations, width: 40, height: 30),              SizedBox(                width: 10,              ),              Column(                mainAxisAlignment: MainAxisAlignment.start,                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  Text(                    item.fillingStationNameAr ?? "",                    style: kTextMedium.copyWith(                      fontSize: 14,                    ),                  ),                  Text(                    item.fillingStationNameEn ?? "",                    style: kTextRegular.copyWith(                      fontSize: 14,                    ),                  ),                ],              ),            ],          ),          SizedBox(            height: 7,          ),          InkWell(              onTap: () async{              await  AppUtils.openMap(item.latitude ?? 0, item.longitude ?? 0);              },              child: Row(                children: [                  kLoadSvgInCirclePath(                    AppIcons.location,                    color: kOrange00,                    height: 20,                    width: 20,                  ),                  Text(                    strings.location_gas_stations,                    style: kTextMedium.copyWith(color: kOrange00, fontSize: 13),                  ),                ],              )),          SizedBox(            height: 7,          ),          SizedBox(            width: 100,            child: AppCupertinoButton(              onPressed: () {                onAddRoundTripFillStation(                    AddRoundTripFillStationPrams(                        id: 0,                        roundTripId: currentRoundTrip.id,                        stationId: item.fillingStationId,                        vehicleZoneId: currentRoundTrip.vehiclesZoneId),                    LatLng(item.latitude ?? 0, item.longitude ?? 0));              },              text: strings.start_but,              elevation: 0,              height: 30,              radius: BorderRadius.circular(10),              textStyle: kTextBold.copyWith(                fontSize: 12,                color: kWhite,              ),              padding: const EdgeInsets.symmetric(vertical: 5),            ),          )        ],      ),    );  }}