 import 'package:shiftapp/presentation/shared/components/index.dart'; import '../../../../domain/entities/index.dart';import '../widgets/tour_history_item.dart';class TourHistoryScreen extends BaseStatelessWidget {  final List<RoundTripDetails> data;  TourHistoryScreen({    Key? key,    required this.data,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      itemBuilder: (context, index) {        return TourHistoryItem(item: data[index]);      },    );  }}