import 'package:dotted_border/dotted_border.dart';import 'package:shiftapp/presentation/presentationUser/resources/colors.dart';import 'package:shiftapp/presentation/presentationUser/resources/constants.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import 'package:shiftapp/utils/app_icons.dart';import '../../../domain/entities/index.dart';class JobDutiesTour extends BaseStatelessWidget {  final List<RoundTypeTermsAndCondition> data;  JobDutiesTour({    Key? key,    required this.data,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return DottedBorder(      color: kGrey_DF,      strokeWidth: 1,      radius: Radius.circular(12),      padding: EdgeInsets.all(20),      borderType: BorderType.RRect,      dashPattern: [20],      child: Column(        mainAxisSize: MainAxisSize.min,        children: data                ?.map(                  (e) => Padding(                    padding: EdgeInsets.only(bottom: e.id == data.last.id ? 0 : 10),                    child: IconText(                      icon: AppIcons.doneCircle,                      iconSize: 16,                      text: e.name ?? '',                      textStyle: kTextMedium.copyWith(                          color: kBlack.withOpacity(0.7), fontSize: 12),                      isExpanded: true,                      sizedBoxWidth: 10,                    ),                  ),                )                .toList() ??            [],      ),    );  }}