import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../common/common_state.dart';import '../bloc/add_work_hazard_bloc.dart';import '../bloc/add_work_hazard_state.dart';import 'add_work_hazard_screen.dart';class AddWorkHazardPage extends BaseBlocWidget<Initialized<AddWorkHazardState>,    AddWorkHazardCubit> {  AddWorkHazardPage({super.key});  @override  void loadInitialData(BuildContext context) {    int? id = getArguments(context);    bloc.fetchInitialData(id);  }  @override  String? title(BuildContext context) {    return strings.add_hazards;  }  @override  Widget buildWidget(      BuildContext context, Initialized<AddWorkHazardState> state) {    return AddWorkHazardScreen(      state: state.data,      onFetchProjectsByCompanyId: (id) {        bloc.fetchProjectsCompanyId(id);      },      onFetchShiftsByProjectId: (id) {        bloc.fetchShiftByProjectId(id);      },      onFetchInstructionsByTypeId: (id) {        bloc.fetchHazardTypeInstructionsByTypeId(id);      },      onAddWorkHazard: (params, files) {        bloc.addWorkHazard(params, files);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}