import 'package:flutter/material.dart';import 'package:percent_indicator/circular_percent_indicator.dart';import 'package:shiftapp/presentation/shared/components/icons/icon_text.dart';import '../../../../../../utils/app_icons.dart';import '../../presentationUser/resources/colors.dart';import '../../presentationUser/resources/constants.dart';import 'base_stateless_widget.dart';import 'decorations/decorations.dart';class circularPercentWidget extends BaseStatelessWidget {  final PercentData percentData;  circularPercentWidget({required this.percentData});  @override  Widget build(BuildContext context) {    return Container(      width: 110,      margin: EdgeInsets.all(3),      padding: EdgeInsets.all(8),      decoration: Decorations.shapeDecoration(radius: 10),      child: CircularPercentIndicator(        header: Padding(          padding: const EdgeInsets.only(bottom: 10),          child: Text(            percentData.text,            style: kTextMedium.copyWith(color: kDark, fontSize: 14),          ),        ),        radius: 30.0,        lineWidth: 4.0,        percent: double.parse(percentData.percent.toString()),        center: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            Text(              percentData.count.toString(),              style: kTextMedium.copyWith(color: kRateBarIconColor, fontSize: 11),            ),            Text(              percentData.label,              style: kTextMedium.copyWith(color: kRateBarIconColor, fontSize: 11),            ),          ],        ),        progressColor: Colors.green,      ),    );  }  Widget iconText({String? text, String? icon}) {    return IconText(      text: strings.statistic_emp,      icon: AppIcons.statisticsEmpOutline,      crossAxisAlignment: CrossAxisAlignment.center,      iconSize: 20,      textStyle: kTextBodyBold.copyWith(color: kRateBarIconColor, fontSize: 16, fontWeight: FontWeight.w700),    );  }}class CircularPercentWidgetV2 extends BaseStatelessWidget {  final PercentData percentData;  final String text;  final String descrption;  CircularPercentWidgetV2({required this.percentData, required this.descrption, required this.text});  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 5),      child: Row(        crossAxisAlignment: CrossAxisAlignment.center,        mainAxisAlignment: MainAxisAlignment.center,        children: [          Expanded(            flex: 2,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              mainAxisAlignment: MainAxisAlignment.center,              children: [                Text(                  text,                  style: kTextRegular.copyWith(color: kBlack, fontSize: 13),                ),                Text(                  descrption,                  style: kTextRegular.copyWith(color: kGray_CD, fontSize: 12),                ),              ],            ),          ),          SizedBox(width: 5),          Expanded(            flex: 1,            child: Container(              height: 110,              child: CircularPercentIndicator(                radius: 25.0,                lineWidth: 4.0,                percent: double.parse(percentData.percent.toString()),                center: Column(                  mainAxisAlignment: MainAxisAlignment.center,                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Text(                      percentData.count.toString(),                      style: kTextMedium.copyWith(color: kBlack, fontSize: 10),                    ),                    Text(                      percentData.label,                      style: kTextMedium.copyWith(color: kGray_CD, fontSize: 10),                    ),                  ],                ),                progressColor: kPrimaryDark,              ),            ),          ),        ],      ),    );  }  Widget iconText({String? text, String? icon}) {    return IconText(      text: strings.statistic_emp,      icon: AppIcons.statisticsEmpOutline,      crossAxisAlignment: CrossAxisAlignment.center,      iconSize: 20,      textStyle: kTextBodyBold.copyWith(color: kRateBarIconColor, fontSize: 16, fontWeight: FontWeight.w700),    );  }}class PercentData {  final num count;  final String text;  final num percent;  final String label;  PercentData({    required this.count,    required this.text,    required this.percent,    required this.label,  });}