import 'package:dropdown_button2/dropdown_button2.dart';import 'package:flutter/material.dart';import '../../../presentationUser/resources/colors.dart';import '../../../presentationUser/resources/constants.dart';class DropDownCustom3 extends StatelessWidget {  final String? hint;  final String? textvalue;  final String? hintText;  final List? items;  final Function(Object?)? onChanged;  final String? validator;  final String? value;  const DropDownCustom3(      {Key? key,        this.hint,        this.value,        this.hintText,        this.textvalue,        this.items,        this.onChanged,        this.validator})      : super(key: key);  @override  Widget build(BuildContext context) {    return Material(      color: Colors.white,      borderRadius: BorderRadius.circular(8),      child: DropdownButtonFormField2(        decoration: kTextFieldDecoration.copyWith(          hintText: hintText!,          enabledBorder: const OutlineInputBorder(            borderSide: BorderSide(color: kGrey_EB, width: 1),          ),          focusedBorder: const OutlineInputBorder(            borderSide: BorderSide(color: kGrey_EB, width: 1),          ),          errorBorder: const OutlineInputBorder(            borderSide: BorderSide(color: kLipStick, width: 1),          ),          focusedErrorBorder: const OutlineInputBorder(            borderSide: BorderSide(color: kLipStick, width: 1),          ),          // suffixIcon: suffixIcon != null ? Icon(suffixIcon, color: kPrimary) : null,        ),        isDense: true,        isExpanded: true,        hint: Text(            hintText!,            style:  kTextMedium.copyWith(fontSize: 13, color: kBattleShipGrey2.withOpacity(.8))        ),        iconStyleData: IconStyleData(          icon: const Icon(            Icons.keyboard_arrow_down,            color: kPrimary,          ),          iconSize: 30,        ),        selectedItemBuilder: (item) {          return items!              .map((item) => Text(            item.name ?? '',            style: kTextRegular.copyWith(                color: kBattleShipGrey2, fontSize: 12),          ))              .toList();        },        value:value ,        items: items!            .map((item) => DropdownMenuItem(          value: item.name,          child: Text(            item.name ?? '',            style: kTextRegular.copyWith(                color: kBattleShipGrey2, fontSize: 12),          ),        ))            .toList(),        validator: (value) {          if (value == null) {            return validator ?? '';          }          return null;        },        onChanged:  onChanged!,      ),    );  }}