import 'package:flutter/cupertino.dart';import '../../../domain/entities/loan_data.dart';import '../../../../components/base_stateless_widget.dart';import '../widgets/loan_item.dart';class ChashiftLoansScreen extends BaseStatelessWidget {  final int type;  final List<LoanData> data;  ChashiftLoansScreen({required this.type, required this.data});  @override  Widget build(BuildContext context) {    return ListView.builder(        shrinkWrap: true,        itemCount: data.length,        itemBuilder: (context, index) {          return LoanItemWidget(            type: type,            data: data[index],          );        });  }}